import './App.css';
import {useEffect, useState} from "react";
import Layout from './hoc/Layout';
import Routes from './Routes';
import Loader from './components/Loader';
import {getData} from './utils'



const App = () => {

    const [memories,setMemories] = useState ([]);
        /*{
          id: 1,
          title:"bali",
          description:"blablablablaa",
          like:true,
          img: 
              "https://www.costakreuzfahrten.ch/content/dam/costa/costa-magazine/articles-magazine/travel/bali-travel/bali_m.jpg.image.694.390.low.jpg"
             ,
          date:"12/5/2017",
          category:"travel"
        },
        {
          id: 2,
          title:"france",
          description:"la vie en rose",
          like:false,
          img: "https://res.cloudinary.com/du5jifpgg/image/upload/t_opengraph_image/Header%20Home/Header-202102/Paristourisme35-4.jpg",
          date:"2/2/2020",
          category:"Family & friends"
        }
    ]);*/
    const [successLoading,setSuccessLoading] = useState(false);
    const[inputSearch,setInputSearch] = useState("");





  useEffect(()=>
    getData().then(data => {
                              setMemories(data);
                              setSuccessLoading(true)
                   })

  ,[]);

     if(!successLoading){
        return (
            <Loader />
        )
     }else{
        return (
                <>

                  <Layout inputSearch={inputSearch}  handleInputSearch={setInputSearch}>
                      <Routes memories ={memories} inputSearch={inputSearch} setMemories={setMemories}/>
                  </Layout>

                </>
        )  

      }
}   
export default App;
